CREATE TABLE logins (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL, username VARCHAR(255) NOT NULL UNIQUE, password VARCHAR(255) NOT NULL, email VARCHAR(255) NOT NULL, role_id BIGINT NOT NULL, islocked VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX role_id_idx (role_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE project_category (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL UNIQUE, startdate DATE, enddate DATE, completetag VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE role (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL UNIQUE, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sessions (id BIGINT AUTO_INCREMENT, sessionname VARCHAR(255) NOT NULL UNIQUE, charter VARCHAR(255) NOT NULL, areas TEXT NOT NULL, testnotes VARCHAR(255), ready VARCHAR(255) NOT NULL, tester VARCHAR(255), status_id BIGINT NOT NULL, project_id BIGINT NOT NULL, strategy_id BIGINT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX status_id_idx (status_id), INDEX project_id_idx (project_id), INDEX strategy_id_idx (strategy_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE status (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL UNIQUE, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE strategy (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL UNIQUE, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
ALTER TABLE logins ADD CONSTRAINT logins_role_id_role_id FOREIGN KEY (role_id) REFERENCES role(id) ON DELETE CASCADE;
ALTER TABLE sessions ADD CONSTRAINT sessions_strategy_id_strategy_id FOREIGN KEY (strategy_id) REFERENCES strategy(id) ON DELETE CASCADE;
ALTER TABLE sessions ADD CONSTRAINT sessions_status_id_status_id FOREIGN KEY (status_id) REFERENCES status(id) ON DELETE CASCADE;
ALTER TABLE sessions ADD CONSTRAINT sessions_project_id_project_category_id FOREIGN KEY (project_id) REFERENCES project_category(id) ON DELETE CASCADE;
